function FAKE (fn_callee, context) {
  /**
   * Returns the fake function corresponding to the replacement of the function passed as first parameter
   * Two possibilities : pass the function as a first parameter, or the name of the function as configured via FAKE.CONFIG
   * A context parameter can also be passed to be used as 'this' operator in the returned function
   */
  var fn_callee_name;
  var args_no = arguments.length;

  if (fn_callee instanceof Function) { // if called with a function, then take its name
    fn_callee_name = fn_callee.name;
  }
  if (typeof fn_callee == 'string' || fn_callee instanceof String) {
    // if called with a string, then use it as function name
    fn_callee_name = fn_callee;
  }

  if (!fn_callee_name) {
    throw ['ERROR: FAKE : first parameter : expected a function or a string (function name)'].join('');
  }

  if (args_no < 2) {
    throw ['ERROR: ', 'FAKE called with only ', args_no, ' arguments'].join('');
  }

  // Basically call whatever stub is configured in FAKE map with
  var fake_f = FAKE.MAP[fn_callee_name];
  if (fake_f) {// if there is a configured fake function, call it with its arguments in the given context
    return function () {
      return fake_f.apply(context, Array.prototype.slice.call(arguments));
    }
  }
  else {
    throw ['ERROR: FAKE : fake stub undefined for function ', fn_callee_name, '!'].join('');
  }
}

FAKE.MAP={};
FAKE.config = function (fn_callee_name, fn_fake) {
  // Limitation : some names cannot be used. For instance, fn_callee_name cannot be any reserved properties of empty objects
  /**
   * Configures a replacement or fake for a given function whose name if passed as a parameter
   * If only one argument, then we return the value presently stored as a fake function
   * otherwise, we set the fake function;
   */
  if ('undefined' !== typeof fn_fake) {
    FAKE.MAP[fn_callee_name] = fn_fake;
  }
  else {
    return FAKE.MAP[fn_callee_name];
  }
};

FAKE.should_be = function (fn) {
  /**
   * returns true : if a fake function is configured for the function whose name is passed as a parameter
   */
  return FAKE.MAP[fn.name] ? true : false;
};

